import React from 'react';
import { useHistory } from 'react-router-dom';
import './HomePage.css';

const HomePage = () => {
  const history = useHistory();

  const navigateTo = (path) => {
    history.push(path);
  };

  return (
    <div className="home">
      <div className="container" onClick={() => navigateTo('/sip-calculator')}>
        <h2>SIP Calculator</h2>
        <p>A SIP calculator helps you estimate the returns on your SIP investments.</p>
      </div>
      <div className="container" onClick={() => navigateTo('/lumpsum-calculator')}>
        <h2>Lumpsum Calculator</h2>
        <p>A Lumpsum calculator helps you estimate the returns on your one-time investments.</p>
      </div>
    </div>
  );
};

export default HomePage;
